import React, { Suspense, useEffect } from "react";
import { makeStyles } from '@material-ui/core/styles';
import "./App.css";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";

import BasicMap from './basic';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import Button from '@material-ui/core/Button';
import grey from '@material-ui/core/colors/grey';
import green from '@material-ui/core/colors/green';
import Grid from '@material-ui/core/Grid';
import TextField from '@material-ui/core/TextField';


const useStyles = makeStyles((theme) => ({
  root: {
    backgroundColor: grey[5],
    borderRadius: 20,
  },
  textx:
  {
    width: '70vw',
 },
  textField:
  {
    height: '30vh',
    width: '80vw',
  },
  butt:
  {
    color: grey[5],
    borderRadius: 15,
    border: '2px solid #00B337',
    width: '20vw',
    '&:hover': {
      backgroundColor: green[600],
      borderColor: green[600],
      boxShadow: 'none',
    },
  },

}));

function App() {
    const classes = useStyles();

    return (      
         <Grid container   direction="row-reverse" justifyContent="space-around" alignItems="center">
            <Card className={classes.root}>

              <BasicMap/>

              <div style={{display:'flex'}}>
              <Grid className={classes.textField} container   direction="row-reverse" justifyContent="space-around" alignItems="center">
                <CardActions>
                     <TextField className={classes.textx}
                      required
                      id="standard-required"
                      label="Откуда"
                      defaultValue=""
                      variant="standard"
                    />
                </CardActions>

                <CardActions>
                  <Button variant="outlined" size="medium" className={classes.butt}>
                    Предсказания
                  </Button>
                </CardActions>

                <CardActions>
                     <TextField className={classes.textx}
                      required
                      id="standard-required"
                      label="Откуда"
                      defaultValue=""
                      variant="standard"
                    />
                </CardActions>

                <CardActions>
                  <Button variant="outlined" size="medium" className={classes.butt} >
                    Оцените Риски
                  </Button>
                </CardActions>
                </Grid>
         </div>

        </Card>

      </Grid>

    );
}

export default App;
